import React, { Component } from 'react';
import "./Test.css"

export default class Test extends Component {
    onDragOver = (ev) => { ev.preventDefault(); };
    onDragStart = (ev) =>  { ev.dataTransfer.setData("text", ev.target.id); };
    onDrop = (ev, ref) =>  {
        ev.stopPropagation();
        ev.preventDefault();
        console.log(ref);
        let rect = ref.getBoundingClientRect();
        let landing_pos = [ev.clientX - rect.left, ev.clientY - rect.top];
        let data = ev.dataTransfer.getData("text");
        let dropper = document.getElementById(data);
        dropper.style.gridColumnStart = '1';
        dropper.style.gridColumnEnd = '4';
        ref.appendChild(document.getElementById(data));
    };

    refs = [React.createRef(), React.createRef()];
    state = {
        rectangles: 2,
    };

    render_GridElements = (x) =>  {
        let gridelements = [];
        for (let i=0; i < x; i++) {
            gridelements.push(
                this.GridElement("GridElement" + i)
            );
        }
        return gridelements;
    };

    GridElement = (key) => {
        return(
            <div
                className={"grid-element"}
                key={key}>
            </div>);
    };

    render_Rectangles = (x) => {
        let rectangles = [];
        for (let i = 0; i < x; i++) {
            rectangles.push(
                <div
                    id={"Rectangle"+i}
                    className={"rectangle"}
                    key={"Rectangle" + i}
                    draggable
                    onDrop={(ev) => {ev.preventDefault(); }}
                    onDragStart={(ev, id) => { this.onDragStart(ev, id); } }>
                    <p>
                        {i}
                    </p>
                </div>
            );
        }
        return rectangles;
    };

    add_Rectangle = () =>  {
        this.setState({rectangles: this.state.rectangles + 1});
    };

    render() {
        return(
            <div className={"Test"}>
                <div className={"grid"}>
                    <div
                        ref={this.refs[0]}
                        className={"grid super-grid"}
                        onDragOver={(ev) => { this.onDragOver(ev); }}
                        onDrop={(ev) => { this.onDrop(ev, this.refs[1]); }}>
                    </div>
                    {this.render_GridElements(9)}
                </div>
                <div className={"start-area-container"}>
                    <div
                        ref={this.refs[1]}
                        onDragOver={(ev) => { this.onDragOver(ev); }}
                        onDrop={(ev) => { this.onDrop(ev, this.refs[1]); }}
                        className={"start-area"}>
                        {this.render_Rectangles(this.state.rectangles)}
                    </div>
                    <div className={"start-area-button"} onClick={this.add_Rectangle}>
                        <p>
                            ELEMENT++
                        </p>
                    </div>
                </div>
            </div>
        );
    }
}
