import React, { Component } from 'react';
import "./Test.css"

const GridElement = (key) => {
    return(
        <div
            className={"grid-element"}
            key={key}
            onDragOver={(ev) => { onDragOver(ev); }}
            onDrop={(ev) => { onDrop(ev); }}>
        </div>);
}

function onDragOver(ev) { ev.preventDefault(); };
function onDragStart(ev, id) { ev.dataTransfer.setData("id", id); };
function onDrop(ev) {
    let id = ev.dataTransfer.getData("id");
    console.log(id);
}

export default class Test extends Component {
    state = {
        rectangles: 1,
    };

    render_GridElements(x) {
        let gridelements = [];
        for (let i=0; i < x; i++) {
            gridelements.push(
                GridElement("GridElement" + i)
            );
        }
        return gridelements;
    }

    render_Rectangles(x) {
        let rectangles = [];
        for (let i = 0; i < x; i++) {
            rectangles.push(
                <div
                    className={"rectangle"}
                    key={"Rectangle" + i}
                    key={"Rectangle" + i}
                    draggable
                    onDragStart={(ev, id) => { onDragStart(ev, id); } }>
                    <p>
                        {i}
                    </p>
                </div>
            );
        }
        return rectangles;
    }

    add_Rectangle = () =>  {
        this.setState({rectangles: this.state.rectangles + 1});
    }

    render() {
        return(
            <div className={"Test"}>
                <div className={"grid"}>
                    {this.render_GridElements(9)}
                </div>
                <div className={"start-area-container"}>
                    <div className={"start-area"}>
                        {this.render_Rectangles(this.state.rectangles)}
                    </div>
                    <div className={"start-area-button"} onClick={this.add_Rectangle}>
                        <p>
                            ELEMENT++
                        </p>
                    </div>
                </div>
            </div>
        );
    }
}
